mapper.select:
select * from attraction_info where sido_code = #{area}
and content_type_id = 32 and content_type_id in (14,15) and content_type_id = 39 and content_type_id = 38 and content_type_id = 28 and content_type_id in (12, 25)
and title like '%${keyword}%'

mapper.update:
update attraction_info
set readcount = readcount + 1
where content_id = #{attractionId}

mapper.select:
select first_image from attraction_info a join
sido s on a.sido_code= s.sido_code where s.sido_name like '%${city}%' AND (first_image IS NOT NULL AND TRIM(first_image) <> '') order by rand() limit 1;
(select s.sido_code, s.sido_name, g.gugun_code, g.gugun_name from sido s join gugun g on s.sido_code = g.sido_code where g.gugun_name like '%${city}%') b on a.sido_code= b.sido_code and a.gugun_code = b.gugun_code where (first_image IS NOT NULL AND TRIM(first_image) <> '') order by rand() limit 1;
